import React, { Component } from 'react';
import { Container, Row, Col, Form } from 'react-bootstrap';


class Body extends Component {
    render() {
        return (
            <div className="calculator">
                <Container>
                    <Row>
                        <Col lg={6}>
                            <Row>
                                <Col lg={8}>
                                    <h5 className="billTitle">Bill</h5>
                                </Col>
                                <Col lg={8}>
                                    <p className="errorBill"></p>
                                </Col>
                            </Row>
                            <Form.Group className="billInput mb-2">
                                <Form.Control type="text" classNmae="bill form-control" placeholder="0.00" aria-label="0.00" aria-describedby="dollarIcon" />
                            </Form.Group>
                            <h5 className="tipTitle">Select Tip %</h5>
                        </Col>
                </Row>
                </Container>
            </div>
        )
    }
}
export default Body;
